import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
const CreateNote=(props)=>{
    const[note,setNote]=useState({
        title:'',
        content:'',
    })

    const InputEvent=(event)=>{
        const {name,value}=event.target
        setNote((preData)=>{
            return{

                ...preData,
                [name]:value,
            }
            
        })
    }

    const addEvent=()=>{
        props.passNote(note);
        setNote({
            title:'',
            content:'',
        })

    }
    return(
        <>
            <div className="main_div">
                <div className="center_div">
                    <form>
                        <input name="title" onChange={InputEvent}   placeholder="Title" autoComplete="off" />
                        <textarea name="content" onChange={InputEvent}  placeholder="Add note" />
                        <Button><AddIcon className="add_icon" onClick={addEvent} /></Button>
                    </form>
                </div>
            </div>

        </>
    )

}
export default CreateNote;
